// E-commerce Store JavaScript
class App {
    constructor() {
        this.init();
    }

    init() {
        this.setupEventListeners();
        this.loadData();
    }

    setupEventListeners() {
        // Add event listeners based on app type

        document.querySelectorAll('.add-to-cart').forEach(btn => {
            btn.addEventListener('click', (e) => {
                this.addToCart(e.target);
            });
        });

        document.querySelector('.checkout-btn').addEventListener('click', () => {
            this.checkout();
        });
    }

    loadData() {
        // Load initial data
        console.log('Loading data for E-commerce Store...');

        this.cart = JSON.parse(localStorage.getItem('cart')) || [];
        this.products = this.getProducts();
        this.renderCart();
    }


    addToCart(button) {
        const productCard = button.closest('.product-card');
        const product = {
            id: Date.now(),
            name: productCard.querySelector('h3').textContent,
            price: productCard.querySelector('.price').textContent,
            quantity: 1
        };

        const existingItem = this.cart.find(item => item.name === product.name);
        if (existingItem) {
            existingItem.quantity += 1;
        } else {
            this.cart.push(product);
        }

        this.saveCart();
        this.renderCart();
        this.showNotification('Product added to cart!');
    }

    removeFromCart(id) {
        this.cart = this.cart.filter(item => item.id !== id);
        this.saveCart();
        this.renderCart();
    }

    renderCart() {
        const cartItems = document.getElementById('cartItems');
        const cartTotal = document.querySelector('.cart-total strong');

        cartItems.innerHTML = this.cart.map(item => `
            <div class="cart-item">
                <span>${item.name} x${item.quantity}</span>
                <span>${item.price}</span>
                <button onclick="app.removeFromCart(${item.id})" class="btn btn-sm btn-danger">Remove</button>
            </div>
        `).join('');

        const total = this.cart.reduce((sum, item) => {
            const price = parseFloat(item.price.replace('$', ''));
            return sum + (price * item.quantity);
        }, 0);

        cartTotal.textContent = `Total: ${utils.formatCurrency(total)}`;
    }

    checkout() {
        if (this.cart.length === 0) {
            alert('Your cart is empty!');
            return;
        }

        alert('Checkout functionality would be implemented here!');
        this.cart = [];
        this.saveCart();
        this.renderCart();
    }

    saveCart() {
        localStorage.setItem('cart', JSON.stringify(this.cart));
    }

    showNotification(message) {
        // Simple notification
        const notification = document.createElement('div');
        notification.textContent = message;
        notification.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            background: #27ae60;
            color: white;
            padding: 10px 20px;
            border-radius: 5px;
            z-index: 1000;
        `;
        document.body.appendChild(notification);

        setTimeout(() => {
            notification.remove();
        }, 3000);
    }
}

