// ============================================================================
// TODO MANAGER - Handles all todo operations with full feature support
// ============================================================================
class TodoManager {
  constructor() {
    this.todos = this.loadTodos()
    this.currentFilter = 'all'
    this.currentSort = 'date'
    this.searchQuery = ''
    console.log('âœ… TodoManager initialized with', this.todos.length, 'todos')
  }

  addTodo(todoData) {
    const todo = {
      id: Date.now(),
      text: todoData.text,
      completed: false,
      createdAt: new Date().toISOString()
    }
    
    // Add optional feature properties
    
    
    
    
    
    
    this.todos.push(todo)
    this.saveTodos()
    console.log('âœ… Todo added:', todo)
    return todo
  }

  deleteTodo(id) {
    this.todos = this.todos.filter(t => t.id !== id)
    this.saveTodos()
    console.log('ğŸ—‘ï¸ Todo deleted:', id)
  }

  toggleTodo(id) {
    const todo = this.todos.find(t => t.id === id)
    if (todo) {
      todo.completed = !todo.completed
      this.saveTodos()
      console.log('âœ… Todo toggled:', id, 'completed:', todo.completed)
    }
  }

  getTodos() {
    let filtered = [...this.todos]
    
    // Apply search filter
    
    if (this.searchQuery) {
      filtered = filtered.filter(todo => 
        todo.text.toLowerCase().includes(this.searchQuery.toLowerCase())
      )
    }
    
    
    // Apply status filter
    
    if (this.currentFilter === 'Active') {
      filtered = filtered.filter(t => !t.completed)
    } else if (this.currentFilter === 'Completed') {
      filtered = filtered.filter(t => t.completed)
    }
    
    
    // Apply sort
    
    if (this.currentSort === 'Sort by Priority' && filtered[0]?.priority) {
      const priorityOrder = { high: 0, medium: 1, low: 2 }
      filtered.sort((a, b) => priorityOrder[a.priority] - priorityOrder[b.priority])
    } else if (this.currentSort === 'Sort by Name') {
      filtered.sort((a, b) => a.text.localeCompare(b.text))
    }
    
    
    return filtered
  }

  
  saveTodos() {
    try {
      localStorage.setItem('dreambuild-todos', JSON.stringify(this.todos))
      console.log('ğŸ’¾ Todos saved to localStorage')
    } catch (error) {
      console.error('Failed to save todos:', error)
    }
  }

  loadTodos() {
    try {
      const saved = localStorage.getItem('dreambuild-todos')
      const todos = saved ? JSON.parse(saved) : []
      console.log('ğŸ“‚ Loaded', todos.length, 'todos from localStorage')
      return todos
    } catch (error) {
      console.error('Failed to load todos:', error)
      return []
    }
  }
  

  getStats() {
    return {
      total: this.todos.length,
      active: this.todos.filter(t => !t.completed).length,
      completed: this.todos.filter(t => t.completed).length
    }
  }
}

// ============================================================================
// UI MANAGER - Handles all rendering and interactions
// ============================================================================
class TodoUI {
  constructor(todoManager) {
    this.todoManager = todoManager
    this.initializeElements()
    this.bindEvents()
    this.render()
  }

  initializeElements() {
    this.form = document.getElementById('add-todo-form')
    this.todoInput = document.getElementById('todo-input')
    this.todosContainer = document.getElementById('todos-container')
    
    // Feature-specific elements (only if features exist)
    
    
    
    
    
    this.searchInput = document.getElementById('search-input');
    this.filterSelect = document.getElementById('filter-select');
    this.sortSelect = document.getElementById('sort-select');
    this.themeToggle = document.getElementById('theme-toggle');
    
    
    console.log('âœ… UI elements initialized:', {
      form: !!this.form,
      todoInput: !!this.todoInput,
      todosContainer: !!this.todosContainer
    })
  }

  bindEvents() {
    // Form submission with retry logic
    if (!this.form) {
      console.warn('âš ï¸ Form not found, retrying in 100ms...')
      setTimeout(() => {
        this.form = document.getElementById('add-todo-form')
        if (this.form) {
          this.bindEvents()
        }
      }, 100)
      return
    }
    
    this.form.addEventListener('submit', (e) => {
      e.preventDefault()
      this.handleAddTodo()
    })
    console.log('âœ… Form submit event bound')

    
    // Search
    if (this.searchInput) {
      this.searchInput.addEventListener('input', (e) => {
        this.todoManager.searchQuery = e.target.value
        this.render()
      })
    }
    

    
    // Filter
    if (this.filterSelect) {
      this.filterSelect.addEventListener('change', (e) => {
        this.todoManager.currentFilter = e.target.value
        this.render()
      })
    }
    

    
    // Sort
    if (this.sortSelect) {
      this.sortSelect.addEventListener('change', (e) => {
        this.todoManager.currentSort = e.target.value
        this.render()
      })
    }
    

    
    // Dark mode toggle
    if (this.themeToggle) {
      this.themeToggle.addEventListener('click', () => {
        document.body.classList.toggle('dark-mode')
        this.themeToggle.textContent = document.body.classList.contains('dark-mode') 
          ? 'â˜€ï¸ Light Mode' 
          : 'ğŸŒ™ Dark Mode'
      })
    }
    

    

    console.log('âœ… Events bound')
  }

  handleAddTodo() {
    const text = this.todoInput.value.trim()
    if (!text) return

    const todoData = { text }
    
    // Add optional feature data
    
    
    
    
    

    this.todoManager.addTodo(todoData)
    this.form.reset()
    this.render()
    this.showNotification('Todo added! âœ…')
  }

  render() {
    const todos = this.todoManager.getTodos()
    
    if (todos.length === 0) {
      this.todosContainer.innerHTML = '<p style="text-align: center; color: #64748b; padding: 3rem;">No todos yet. Add one above! ğŸ“</p>'
    } else {
      this.todosContainer.innerHTML = todos.map(todo => this.renderTodo(todo)).join('')
    }

    this.updateStats()
  }

  renderTodo(todo) {
    const priorityColors = { high: '#ef4444', medium: '#f59e0b', low: '#10b981' }
    const priorityIcons = { high: 'ğŸ”´', medium: 'ğŸŸ¡', low: 'ğŸŸ¢' }
    
    let html = '<div class="todo-item" style="border-left: 4px solid ' + (priorityColors[todo.priority] || '#64748b') + '; padding: 1rem; margin-bottom: 1rem; background: #f8fafc; border-radius: 8px;">'
    html += '<div style="display: flex; align-items: start; gap: 1rem;">'
    html += '<input type="checkbox" ' + (todo.completed ? 'checked' : '') + ' onchange="todoApp.ui.handleToggle(' + todo.id + ')" style="margin-top: 0.25rem; width: 20px; height: 20px; cursor: pointer;" />'
    html += '<div style="flex: 1;">'
    html += '<div style="display: flex; justify-content: space-between; align-items: start;">'
    html += '<h4 style="margin: 0; ' + (todo.completed ? 'text-decoration: line-through; color: #94a3b8;' : 'color: #1e293b;') + '">' + todo.text + '</h4>'
    html += '<button onclick="todoApp.ui.handleDelete(' + todo.id + ')" class="btn btn-secondary" style="padding: 0.25rem 0.75rem; font-size: 0.875rem;">ğŸ—‘ï¸</button>'
    html += '</div>'
    
    
    
    
    
    
    
    
    
    
    
    html += '</div></div></div>'
    return html
  }

  handleToggle(id) {
    this.todoManager.toggleTodo(id)
    this.render()
  }

  handleDelete(id) {
    this.todoManager.deleteTodo(id)
    this.render()
    this.showNotification('Todo deleted! ğŸ—‘ï¸')
  }

  

  updateStats() {
    const stats = this.todoManager.getStats()
    document.getElementById('total-count').textContent = stats.total
    document.getElementById('active-count').textContent = stats.active
    document.getElementById('completed-count').textContent = stats.completed
  }

  showNotification(message) {
    const notification = document.createElement('div')
    notification.style.cssText = 'position: fixed; top: 20px; right: 20px; background: #10b981; color: white; padding: 1rem 1.5rem; border-radius: 8px; box-shadow: 0 10px 25px rgba(0,0,0,0.1); z-index: 1000; animation: fadeIn 0.3s;'
    notification.textContent = message
    document.body.appendChild(notification)
    setTimeout(() => {
      notification.style.opacity = '0'
      notification.style.transform = 'translateY(-20px)'
      notification.style.transition = 'all 0.3s'
      setTimeout(() => notification.remove(), 300)
    }, 2000)
  }
}

